export default {
    sequelize: getSequelizeConfig(),
}

function getSequelizeConfig() {
    return {
        database: process.env.DB_NAME || "pets4ever",
        username: process.env.DB_USERNAME || "pets4ever",
        password: process.env.DB_PASSWORD || "pets4ever",
        options: {
            host: process.env.DB_HOST || "db",
            port: process.env.DB_PORT || "3306",
            dialect: process.env.DB_DIALECT || "mysql",
            pool: {
                max: 5,
                min: 0,
                idle: 10000,
            },
        }
    };
}