import {Routing} from "../../Routing";
import {MakeContactCommandSystem} from "application/system/contact/ContactCommandSystem";
export default class ContactRouting implements Routing {
    public handlers() {
        return {
            createContact: (req: any) => {
                const contactData = {
                    contactId: req.body.contactId,
                    surName: req.body.surName,
                    name: req.body.name,
                    email: req.body.email,
                    phoneNumber: req.body.phoneNumber,
                    message: req.body.messages,
                    contactDate: req.body.contactDate
                };
                return MakeContactCommandSystem().createContact(contactData);
            }
        };
    }

    public fillRoutes(router: any, handlers: any) {
        router.post("/contact", handlers.createContact);
        /**/
    }
}
