import {Routing} from  "../../Routing";
import {MakeInfoAllPageCommandSystem} from "application/system/infoAllPage/InfoAllPageCommandSystem";
import {MakeInfoAllPageQuerySystem} from "application/system/infoAllPage/InfoAllPageQuerySystem";

export default class InfoAllPageRouting implements Routing {
    public handlers () {
        return {
            createInfoPage: (req: any) => {
                const infoPage = {
                    id: req.body.id,
                    dataInfo: req.body.dataInfo
                };
                return MakeInfoAllPageCommandSystem().createInfoAllPage(infoPage);
            },
            getInfoAllPage: (req: any) => MakeInfoAllPageQuerySystem().getInfoAllPage(),
        }
    }
    public fillRoutes(router: any, handlers: any) {
        router.post("/infoPage", handlers.createInfoPage);
        router.get("/infoPage", handlers.getInfoAllPage);
        /**/
    }
}