import {CreateUserData} from "domain/user/User";
import {MakeUserGateway, UserGateway} from "gateway/user/UserGateway";
import {InvalidArgumentException} from "domain/Exception";

export function MakeUserQuerySystem(gateway?: UserGateway) {
    return new UserQuerySystem(gateway || MakeUserGateway());
}

class UserQuerySystem {
    private gateway: UserGateway;

    constructor(gateway: UserGateway) {
        this.gateway = gateway;
    }

    public validateUser(email: string, password: string) {
        const user = this.gateway.validateUser(email, password);
        return user;    
    }
}
