import {CreateContactData} from "domain/contact/Contact";
import {MakeContactGateway, ContactGateway} from "gateway/contact/ContactGateway";

export function MakeContactCommandSystem (gateway?: ContactGateway){
    return new ContactCommandSystem(gateway || MakeContactGateway());
}

class ContactCommandSystem {
    private gateway: ContactGateway;

    constructor(gateway: ContactGateway) {
        this.gateway = gateway;
    }

    public createContact(data: CreateContactData) {
        console.log("data===>", data);
        const newContact = this.gateway.create(data);
        return newContact;
    }
}