import {CreateInfoAllPage} from "domain/infoAllPage/InfoAllPage";
import {MakeInfoAllPage, InfoAllPageGateway} from "gateway/infoAllPage/InfoAllPage";

export function MakeInfoAllPageCommandSystem (gateway?: InfoAllPageGateway){
    return new InfoAllPageCommandSystem (gateway || MakeInfoAllPage());
}
class InfoAllPageCommandSystem {
    private gateway: InfoAllPageGateway;

    constructor (gateway: InfoAllPageGateway){
        this.gateway = gateway;
    }

    public createInfoAllPage (data: CreateInfoAllPage){
        const newInfoPage = this.gateway.CreateInfoPage(data);
        return newInfoPage;
    }
}